INSERT INTO `city` VALUES (1,'ПРЕДСТАВЛЕНИЕ О ГОРОДЕ','<blockquote>\r\n	<p style=\"text-align: center;\">\r\n		<strong>ПРЕДСТАВЛЕНИЕ О ГОРОДЕ</strong><br />\r\n		<br />\r\n		&nbsp;</p>\r\n	<p>\r\n		<strong><em><a href=\"#location\">Географическое положение</a></em></strong><br />\r\n		<strong><em><a href=\"#square\">Площадь</a></em></strong><br />\r\n		<strong><em><a href=\"#name_source\">Название города</a></em></strong><br />\r\n		<strong><em><a href=\"#climate\">Климат</a></em></strong><br />\r\n		<strong><em><a href=\"#temprature_clothes\">Температура воздуха и необходимая одежда</a></em></strong><br />\r\n		<strong><em><a href=\"#geo\">Рельеф </a></em></strong><br />\r\n		<strong><em><a href=\"#water\">Гидротехнические ресурсы</a></em></strong><br />\r\n		<strong><em><a href=\"#industry_finance\">Промышленный и финансовый потенциал Даляня</a></em></strong><br />\r\n		<strong><em><a href=\"#population\">Население и трудоустройство</a></em></strong><br />\r\n		<strong><em><a href=\"#economy\">Общее состояние экономики</a></em></strong></p>\r\n	<p style=\"margin-left: 40px;\">\r\n		<strong><em><a href=\"#economy_1\">Экономическая ситуация</a></em></strong><br />\r\n		<strong><em><a href=\"#economy_2\">Городской бюджет</a></em></strong><br />\r\n		<strong><em><a href=\"#economy_3\">Индекс потребительских цен </a></em></strong></p>\r\n	<p>\r\n		<strong><em><a href=\"#religion\">Религия</a></em></strong><br />\r\n		<strong><em><a href=\"#culture\">Культурные мероприятия</a></em></strong><br />\r\n		<strong><em><a href=\"#tourism\">Туризм</a></em></strong><br />\r\n		<strong><em><a href=\"#edu\">Естественно-научное образование </a></em></strong><br />\r\n		<strong><em><a href=\"#uni\">Высшие учебные заведения Даляня</a></em></strong><br />\r\n		<strong><em><a href=\"#school\">Институты Даляня</a></em></strong><br />\r\n		<strong><em><a href=\"#dialect\">Далянский диалект</a></em></strong><br />\r\n		<strong><em><a href=\"#diplomat\">Хронология возникновения дружественных связей с городами - побратимами</a></em></strong><br />\r\n		<strong><em><a href=\"#interest_stuff\">Самое-самое в Даляне и о Даляне</a></em></strong></p>\r\n</blockquote>\r\n<blockquote>\r\n	<p style=\"text-align: center;\">\r\n		<br />\r\n		<br />\r\n		<em><strong>Краткая&nbsp;характеристика города&nbsp;Даляня</strong></em><br />\r\n		&nbsp;</p>\r\n	<table align=\"center\" border=\"1\" style=\"width: 200px;\">\r\n		<tbody>\r\n			<tr>\r\n				<td>\r\n					<p style=\"margin-bottom: 0cm;\">\r\n						<span style=\"font-size: 12px;\">&nbsp;</span><span style=\"font-size: 12px;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;</span></span><span style=\"font-size: 12px;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><font class=\"Apple-style-span\"><font style=\"font-size: 9pt;\"><span lang=\"ru-RU\"><strong><span class=\"Apple-style-span\"><font style=\"font-size: 9pt;\"><span lang=\"ru-RU\">Прежние названия</span></font></span></strong><span class=\"Apple-style-span\"><font style=\"font-size: 9pt;\">：</font></span></span></font></font></span><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;</span></span></p>\r\n				</td>\r\n				<td>\r\n					<p style=\"margin-left: 80px;\">\r\n						<span style=\"font-size: 12px;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span class=\"Apple-style-span\">&nbsp; &nbsp;&nbsp;&nbsp; Дальний&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></span></span></p>\r\n				</td>\r\n				<td>\r\n					<span style=\"font-size: 12px;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Диалект</strong>：</span></span></td>\r\n				<td style=\"text-align: center;\">\r\n					<span style=\"font-size: 12px;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Мандаринское наречие Jiaoliao</span></span></td>\r\n			</tr>\r\n			<tr>\r\n				<td>\r\n					<span style=\"font-size: 12px;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;<strong>Административная категория</strong>：</span></span></td>\r\n				<td style=\"width: 200px; text-align: center;\">\r\n					<span style=\"font-size: 12px;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;Второй по значимости город в провинции, город спец.назначения</span></span></td>\r\n				<td rowspan=\"2\">\r\n					<span style=\"font-size: 12px;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;<strong>Климат</strong>：</span></span></td>\r\n				<td rowspan=\"2\" style=\"text-align: center;\">\r\n					<span style=\"font-size: 12px;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;Морской теплый умеренный муссонный климат</span></span></td>\r\n			</tr>\r\n			<tr>\r\n				<td>\r\n					<span style=\"font-size: 12px;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;<strong>Провинция</strong>：</span></span></td>\r\n				<td style=\"text-align: center;\">\r\n					<span style=\"font-size: 12px;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;Провинция Ляонин, Китай</span></span></td>\r\n			</tr>\r\n			<tr>\r\n				<td>\r\n					<span style=\"font-size: 12px;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;<strong>Телефонный код</strong>：</span></span></td>\r\n				<td style=\"text-align: center;\">\r\n					<span style=\"font-size: 12px;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;0411</span></span></td>\r\n				<td rowspan=\"2\">\r\n					<span style=\"font-size: 12px;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;<strong>Аэропорт</strong>：</span></span></td>\r\n				<td rowspan=\"2\" style=\"text-align: center;\">\r\n					<span style=\"font-size: 12px;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;Международный аэропортЧжоушуйцзы</span></span></td>\r\n			</tr>\r\n			<tr>\r\n				<td>\r\n					<span style=\"font-size: 12px;\"><strong><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;Почтовый код：</span></strong></span></td>\r\n				<td style=\"text-align: center;\">\r\n					<span style=\"font-size: 12px;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;116000</span></span></td>\r\n			</tr>\r\n			<tr>\r\n				<td>\r\n					<span style=\"font-size: 12px;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;<strong>Географическое положение</strong>：</span></span></td>\r\n				<td style=\"text-align: center;\">\r\n					<span style=\"font-size: 12px;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;Южная часть<br />\r\n					Полуострова Ляодун</span></span></td>\r\n				<td>\r\n					<span style=\"font-size: 12px;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;<strong>Автомобильный код</strong>：</span></span></td>\r\n				<td style=\"text-align: center;\">\r\n					<span style=\"font-size: 12px;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;辽B</span></span></td>\r\n			</tr>\r\n			<tr>\r\n				<td>\r\n					<span style=\"font-size: 12px;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;<strong>Площадь</strong>：</span></span></td>\r\n				<td style=\"vertical-align: middle; text-align: center;\">\r\n					&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<span style=\"font-size: 12px;\"><span style=\"font-family: arial,helvetica,sans-serif;\">13237 тыс&nbsp; кв.км</span></span></td>\r\n				<td>\r\n					<span style=\"font-size: 12px;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;<strong>Международный телефонный код</strong>：</span></span></td>\r\n				<td style=\"text-align: center;\">\r\n					<span style=\"font-size: 12px;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;+86-411</span></span></td>\r\n			</tr>\r\n			<tr>\r\n				<td>\r\n					<span style=\"font-size: 12px;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;<strong>Население</strong>：</span></span></td>\r\n				<td style=\"text-align: center;\">\r\n					<span style=\"font-size: 12px;\"><span style=\"font-family: arial,helvetica,sans-serif;\">6200000человек(2008г)</span></span></td>\r\n				<td>\r\n					<span style=\"font-size: 12px;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Дерево города</strong>：<br />\r\n					<span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;<strong>Цветок города</strong>：</span></span></span></td>\r\n				<td style=\"text-align: center;\">\r\n					<span style=\"font-size: 12px;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Софора японская<br />\r\n					<span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;Роза</span></span></span></td>\r\n			</tr>\r\n		</tbody>\r\n	</table>\r\n	<p style=\"text-align: center;\">\r\n		&nbsp;</p>\r\n	<p style=\"text-align: justify;\">\r\n		<strong><em><span id=\"location\">Географическое положение</span></em></strong></p>\r\n	<p style=\"text-align: justify;\">\r\n		<br />\r\n		&nbsp;&nbsp;&nbsp;&nbsp; Город Далянь расположен на южной части Ляодунского полуострова на Северо-Востоке Китая. С трех сторон город окружен морем: на востоке он омывается Жёлтым морем, на западе &mdash; проливом Бохай. На противоположном берегу &ndash; Шаньдунский полуостров. На севере город граничит с обширной Северо-Восточной равниной. Далянь, как важный порт и индустриальный, торговый и туристический центр, является главными воротами не только для северо-восточного&nbsp; Китая, но и имеет мировую значимость.<br />\r\n		&nbsp;</p>\r\n	<p style=\"text-align: justify;\">\r\n		<strong><em><span id=\"square\">Площадь</span></em></strong></p>\r\n	<p style=\"text-align: justify;\">\r\n		<br />\r\n		&nbsp;&nbsp;&nbsp;&nbsp; Общая площадь Далянь, включающего административно подчиненные городу шесть районов, три города и один уезд, составляет 13,237 тыс. кв. км, в т.ч.: площадь собственно города Далянь &ndash; 2415 кв.км.<br />\r\n		&nbsp;</p>\r\n	<p style=\"text-align: justify;\">\r\n		<strong><em><span id=\"name_source\">Название города</span></em></strong></p>\r\n	<p style=\"text-align: justify;\">\r\n		<br />\r\n		&nbsp;&nbsp;&nbsp;&nbsp; Даляню насчитывают чуть более 100 лет. Самое раннее название города, которое можно найти &ndash; Зеленая Нива (青泥洼)。С 1898г русские распланировали город для дальнейшей застройки, начали углублять гавань, строить причалы, волноломы, верфи, прокладывали железнодорожное полотно. В 1903г закончилось строительство железнодорожной ветки, соединившей Порт-артур и Далянь с Харбином. В то время Далянь назывался Дальний. После русско-японсной войны Япония переименовала название города в Далянь, которое и сохранилось до сих пор.<br />\r\n		<br />\r\n		<strong><em><span id=\"climate\">Климат</span></em></strong><br />\r\n		<br />\r\n		&nbsp;&nbsp;&nbsp;&nbsp; Далянь расположен в северном умеренном поясе, преобладает континентально-муссонный климат с морским характером: зимой не морозно, летом не жарко, 4 времени года отличаются друг от друга. Годовая средняя температура &mdash; 10.5℃, годовой объём осадков &mdash; 550&mdash;950мм., годовое количество солнечного освещения &mdash; 2500&mdash;2800 часов.<br />\r\n		<br />\r\n		<strong><em><span id=\"temprature_clothes\">Температура воздуха и необходимая одежда</span></em></strong><br />\r\n		<br />\r\n		&nbsp;&nbsp;&nbsp;&nbsp; Зима (конец ноября &ndash; начало марта), в данный период самая низкая температура&nbsp; -14-17℃, туристам, собирающимся приехать в данный сезон необходимо захватить с собой пуховики, свитера, перчатки, джинсы, теплые трекотажные брюки, а также теплую и удобную спортивную обувь.<br />\r\n		&nbsp;&nbsp;&nbsp;&nbsp; Весна (март - май), в данный сезон средняя тепмпература 15℃, туристам, собирающимся приехать в данный сезон необходимо захватить с собой куртку, свитер, кофты или рубашки с длинным рукавом, джинсы.<br />\r\n		&nbsp;&nbsp;&nbsp;&nbsp; Лето (июнь &ndash; начало сентября), в данный сезон самая жаркая тепмпература воздуха 30-32℃, туристам, собирающимся приехать в данный сезон необходимо захватить с собой футболки, рубашки с коротким руковом, юбки, кепки, шляпы от солнца, солнечные очки<br />\r\n		<br />\r\n		<strong><em><span id=\"geo\">Рельеф</span> </em></strong><br />\r\n		<br />\r\n		&nbsp;&nbsp;&nbsp;&nbsp; В Даляне больше горных и холмистых районов, меньше равнинных и низинных. Северная часть более высокая и широкая, а южная &ndash; низкая и узкая. Рельеф имеет основной наклон с центральной оси на Жёлтое море с юго-восточной стороны и на Бохай с северо-западной стороны. Конец горной цепи Цяньшань &ndash; гора Чанбайшань проходит через Далянь. Большая часть Даляня &ndash; горные районы и низкие холмы, только в долинах между горами и в местностях между рекой и морем разрозненно расположены равнины и низины. Очень развит карстовый рельеф.<br />\r\n		<br />\r\n		<strong><em><span id=\"water\">Гидротехнические ресурсы</span></em></strong><br />\r\n		<br />\r\n		&nbsp;&nbsp;&nbsp;&nbsp; Большая часть рек Даляня относится к бассейнам Жёлтого моря и моря Бохай. В Жёлтое море впадают большие реки Билю, Инна, Чжуан, Цзаньцзи, Даша, Дэнша, Циншуй, Малан и др., а в море Бохай&mdash;Фучжоу, Лигуаньцунь, Саньшилипу и др., в том числе самая большая река Билю&mdash; источник водных ресурсов для сообщения между бассейнами. Кроме вышеуказанных рек в этом районе насчитывается ещё 200 небольших речек. Общее количество запасов пресных вод в Даляне ежегодно составляет 3.786 млрд. куб. м. , в том числе поверхностных вод&mdash; 3.42 млрд. куб. м. , подземных вод&mdash;884 млн. куб. м. , вод повторного использования &mdash; 580 млн. куб. м.<br />\r\n		<br />\r\n		<strong><em><span id=\"industry_finance\">Промышленный и финансовый потенциал Даляня</span></em></strong><br />\r\n		<br />\r\n		&nbsp;&nbsp;&nbsp;&nbsp; Далянь - один из крупнейших промышленных центров провинции Ляонин и Северо-востока Китая в целом. В городе размещено 150 промышленных отраслей и около 10 000 промышленных предприятий, из которых 3000 предприятий тяжёлой промышленности, вчастности машиностроения, металлургии, судостроения, нефтехимической, электронной и легкой промышленности, выпускающие разнообразные нефтехимическую продукцию, дизельные локомотивы, цветные телевизоры и другие товары.<br />\r\n		&nbsp;&nbsp;&nbsp;&nbsp; Государственная политика промышленного сектора далянского региона направлена на поощрение инвестиционных вложений в следующие отрасли промвышленности - лёгкая инженерная, электронная, нефтехимическая, производство лекарств и строительных материалов. Поощряются также вложения инвестиций в инфраструктуру, коммерцию, торговлю, туризм и реформу госпредприятий.<br />\r\n		&nbsp;&nbsp;&nbsp;&nbsp; Что касается отраслей индустрии, то приоритетными являются следующие отрасли и направления:&nbsp;<br />\r\n		<span style=\"font-size: 9px;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<font size=\"1\">&nbsp;&nbsp;</font>◆</span>&nbsp;&nbsp;отрасль новых материалов,<br />\r\n		<span style=\"font-size: 9px;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<font size=\"1\">&nbsp;&nbsp;</font> ◆</span>&nbsp; отрасль информации и коммуникаций,<br />\r\n		<span style=\"font-size: 9px;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<font size=\"1\">&nbsp;&nbsp;</font> ◆</span>&nbsp; освоения моря,<br />\r\n		<span style=\"font-size: 9px;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<font size=\"1\">&nbsp;&nbsp;</font>◆</span>&nbsp;энергосберегажщая,&nbsp;<br />\r\n		<span style=\"font-size: 9px;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<font size=\"1\">&nbsp;&nbsp;</font>◆</span>&nbsp;отрасль биотехнологий,&nbsp;<br />\r\n		<span style=\"font-size: 9px;\">&nbsp;&nbsp;&nbsp;&nbsp;<font size=\"1\">&nbsp;&nbsp;</font>&nbsp;&nbsp;&nbsp;&nbsp;◆</span>&nbsp;охраны окружающей среды,<br />\r\n		<span style=\"font-size: 9px;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<font size=\"1\">&nbsp;&nbsp;</font>&nbsp; ◆</span>&nbsp;возобновления ресурсов и комплексного их использования,<br />\r\n		<span style=\"font-size: 9px;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<font size=\"1\">&nbsp;&nbsp;</font>&nbsp;&nbsp; ◆</span>&nbsp;отрасль микроэлектронной индустрии.<br />\r\n		&nbsp;&nbsp;&nbsp; По сравнению с другими районами Китая, расходы на производство промышленной продукции, включая стоимость аренды земли, электроэнергии и рабочей силы считаются низкими в Далянской зоне развития.<br />\r\n		&nbsp;&nbsp;&nbsp; Низкая стоимость рабочей силы, объясняется имеющимися большими её ресурсами, а именно ежегодный прирост, которых составляет 100 000 человек, при этом 70 % имеют высшее и среднее образование.<br />\r\n		&nbsp;&nbsp;&nbsp;&nbsp; Город Далянь имеет также высокий уровень научно-технического потенциала. Это один из районов с самой высокой научной и технической базой, где сосредоточено большое количество учёных и техников. Здесь работают более 20 академиков Академии наук Китая и инженерной Академии Китая,&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; находятся 17 университетов и институтов, боле 300 научно-исследовательских институтов и прфессионально-технических училищ.<br />\r\n		&nbsp;&nbsp;&nbsp;&nbsp; Отличительной чертой Даляня от всех других городов в северо-восточном Китае является общая сумма капиталовложений,&nbsp; которая превышает средний показатель аналогичных районов. Более 10 зарубежных банков, таких, например как, Банк Токио и&nbsp; Торгово - промышленный банк, успешно реализуют стратегию по привлечению иностранных инвестиций. К настоящему времени в городе зарегистрировано более 5700 предприятий с участием иностранного капитала на общую сумму около 10 млрд.долл. По эффективности использования иностранных инвентиций&nbsp; Далянь занимает первое место в КНР.<br />\r\n		<br />\r\n		<strong><em><span id=\"population\">Население и трудоустройство</span></em></strong><br />\r\n		<br />\r\n		&nbsp;&nbsp;&nbsp; В городе проживает 6,2 миллионов постоянного населения. На конец года на государственных и коллективных предприятиях были заняты 883 тысяч работников. За истекший год проведена регистрация новых предприятий с занятостью 12.363 тысяч человек, зарегистрированный капитал 519 миллионов юаней. В течение года 161 тысяча человек были трудоустроены<br />\r\n		<br />\r\n		&nbsp;</p>\r\n</blockquote>\r\n<blockquote>\r\n	<p style=\"text-align: center;\">\r\n		<strong><span id=\"economy\">Общее состояние экономики</span></strong></p>\r\n	<p style=\"text-align: center;\">\r\n		<img alt=\"\" src=\"/images/editorimages/1285685089435.jpg\" /></p>\r\n	<p style=\"text-align: justify;\">\r\n		<strong><em><span id=\"economy_1\">Экономическая ситуация</span></em></strong><br />\r\n		<br />\r\n		&nbsp;&nbsp;&nbsp;&nbsp; Народное хозяйство города развивается быстрыми темпами. По предварительным расчётам общий объем производства в рамках всего города достиг 313.1 млрд. юаней, прирост составил 17,5 % по сравнению с прошлым годом (это самый быстрый темп экономического роста с 1994 года).<br />\r\n		<br />\r\n		<strong><em><span id=\"economy_2\">Городской бюджет</span></em></strong><br />\r\n		<br />\r\n		&nbsp;&nbsp;&nbsp;&nbsp; Общий бюджетный доход всего города достиг 26.8 млрд. юаней, прирост составил 36,6% (это самый большой прирост с 1985 года, когда Далянь получил особый экономический статус), в том числе на уровне города---12.12 млрд. юаней, (прирост 34.8 %), на уровне уезда---14.68 млрд. юаней (прирост достиг 38.1 %). Общая сумма расходных смет достигла 34.46 млрд. юаней, (прирост в 29.3 % по сравнению с прошлым годом).<br />\r\n		<br />\r\n		<strong><em><span id=\"economy_3\">Индекс потребительских цен </span></em></strong><br />\r\n		<br />\r\n		&nbsp;&nbsp;&nbsp;&nbsp; Потребительский индекс повышается по структуре, отраженной в схеме 1.<br />\r\n		&nbsp;</p>\r\n	<p style=\"text-align: center;\">\r\n		<em><strong>Схема 1: Общий индекс потребления жителей</strong></em><br />\r\n		&nbsp;</p>\r\n	<p style=\"text-align: center;\">\r\n		&nbsp;</p>\r\n	<table align=\"center\" border=\"1\" cellpadding=\"1\" cellspacing=\"1\" style=\"width: 200px;\">\r\n		<tbody>\r\n			<tr>\r\n				<td style=\"text-align: center;\">\r\n					<p>\r\n						<br />\r\n						<strong>Показатель<br />\r\n						&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</strong></p>\r\n				</td>\r\n				<td style=\"text-align: center;\">\r\n					<strong>&nbsp;<br />\r\n					По сравнению с прошлым годом<br />\r\n					(в %)<br />\r\n					&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<br />\r\n					&nbsp;&nbsp;</strong></td>\r\n			</tr>\r\n			<tr>\r\n				<td>\r\n					общий индекс потребления жителей（%）&nbsp;</td>\r\n				<td style=\"text-align: center;\">\r\n					104.0&nbsp;</td>\r\n			</tr>\r\n			<tr>\r\n				<td>\r\n					&nbsp;в т.ч.: индекс цен потребительных продуктов</td>\r\n				<td style=\"text-align: center;\">\r\n					&nbsp;104.5</td>\r\n			</tr>\r\n			<tr>\r\n				<td>\r\n					&nbsp;индекс цен бытового обслуживания</td>\r\n				<td style=\"text-align: center;\">\r\n					&nbsp;102.7</td>\r\n			</tr>\r\n			<tr>\r\n				<td>\r\n					&nbsp;в т.ч.: пищевые товары</td>\r\n				<td style=\"text-align: center;\">\r\n					&nbsp;110.2</td>\r\n			</tr>\r\n			<tr>\r\n				<td>\r\n					&nbsp;сигареты, спиртные напитки и их тара</td>\r\n				<td style=\"text-align: center;\">\r\n					&nbsp;100.0</td>\r\n			</tr>\r\n			<tr>\r\n				<td>\r\n					&nbsp;одежда</td>\r\n				<td style=\"text-align: center;\">\r\n					&nbsp;97.5</td>\r\n			</tr>\r\n			<tr>\r\n				<td>\r\n					&nbsp;бытовая техника, ее обслуживание и<br />\r\n					ремонт</td>\r\n				<td style=\"text-align: center;\">\r\n					&nbsp;102.3</td>\r\n			</tr>\r\n			<tr>\r\n				<td>\r\n					&nbsp;услуги в сфере здравоохранения</td>\r\n				<td style=\"text-align: center;\">\r\n					&nbsp;102.9</td>\r\n			</tr>\r\n			<tr>\r\n				<td>\r\n					&nbsp;транспорт и связь</td>\r\n				<td style=\"text-align: center;\">\r\n					&nbsp;98.3</td>\r\n			</tr>\r\n			<tr>\r\n				<td>\r\n					&nbsp;Услуги в сфере развлечений, образования, культуры и обслуживания</td>\r\n				<td style=\"text-align: center;\">\r\n					&nbsp;97.6</td>\r\n			</tr>\r\n			<tr>\r\n				<td>\r\n					Цены на коммунальные услуги</td>\r\n				<td style=\"text-align: center;\">\r\n					&nbsp;104.7</td>\r\n			</tr>\r\n		</tbody>\r\n	</table>\r\n	&nbsp;\r\n	<p style=\"text-align: justify;\">\r\n		<br />\r\n		<br />\r\n		<strong><em><span id=\"religion\">Религия</span></em></strong><br />\r\n		<br />\r\n		&nbsp; &nbsp;&nbsp;&nbsp; В Даляне одновременно мирно развивается несколько религий, таких как буддизм, даосизм, христианство, ислам, католицизм и другие.<br />\r\n		&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Даосизм, буддизм в Даляне насчитывает уже более чем тысячелетную историю, христианство и католицизм появились в Даляне во времена династии Мин и Цин.<br />\r\n		&nbsp;&nbsp;&nbsp;&nbsp; Перед историческим освобождением города, в области Даляня были более 400 монастырей и храмов, в начеле освобождения были более 230 монастырей и храмов. После образования нового Китая, в г.Далянь (Люйда) была осуществлена политика религии партии, с тех пор стала уважаться свобода религиозного вероисповедания, религия была разрешена легально. В период &laquo;Культурной революции&raquo;, много монастырей и храмов разобрали. Но после 3-го пленума ЦК КПК 11-го созыва, вторично была подтверждена политика партии о свободе религиозного верования, легальные религии получили законную защиту. Были сняты несправедливые обвинения и оправданы религиозные деятели &laquo;Культурной революции&raquo;, восстоновили комиссию католичества и комиссию христианства, и создали ассоциацию буддизма и ассоциацию ислама, призвали религиозных людей активно участвовать в строительстве социалистической материальной и духовной цивилизации. К 1997 году, в общем городе имело 72 места для религиозной деятельности, 6 религиозных групп, 55 тысяч приверженцев, 110 человек религиозных преподавателей и служащих.<br />\r\n		<br />\r\n		<strong><em><span id=\"culture\">Культурные мероприятия</span></em></strong><br />\r\n		&nbsp;<br />\r\n		&nbsp;&nbsp;&nbsp; 1. Площадь далянской библиотеки 30245 кв.м., в 1990 году началась использоваться, была государственной библиотекой первого класса. Имеет 2,1 миллионов книг, 70 тысяч читателей, ежегодно принимает &nbsp;800 тысяч человек. 1999 году, преобразована в &quot;Китайско-японский литературный музей,&quot; и на базе ее создан &quot;Филиал дома молодежи&quot;.<br />\r\n		&nbsp;<br />\r\n		&nbsp;&nbsp;&nbsp; 2. Площадь далянской детсткой библиотеки 6500 кв.м., в 1997 году началась использоваться, была государственной библиотекой первого класса. Имеет 310 тысяч книг, 17 тысяч читателей, ежегодвно принает 50 тысяч человек.<br />\r\n		&nbsp;<br />\r\n		&nbsp;&nbsp;&nbsp; 3. Далянская выставка искусства, её площадь составляет 1400 кв.м., в 1997 году началась использоваться, ежегодвно проводит более десячи выставок, уже стала искусственным дворцом в Даляне.<br />\r\n		&nbsp;<br />\r\n		&nbsp;&nbsp;&nbsp; 4. Народный клуб культур, его площадь составляет 12000 кв. м., используется с 1950 года, 1995 году проведена полная реконструкция. После реконструкции, разнообразились и усилились функции, мероприятия стали красочнее и богаче, кдуб встречает много руководителей как из нутри страны, так и из-за рубежа, в том числе и известные культурные группы.<br />\r\n		&nbsp;<br />\r\n		<strong><em><span id=\"tourism\">Туризм</span></em></strong><br />\r\n		&nbsp;<br />\r\n		&nbsp;&nbsp;&nbsp;&nbsp; Далянь расположен на южной оконечности Ляодунского полуострова северо-восточного Китая и построен на основании порта. С трех сторон его окружает море. На востоке он омывается Жёлтым морем, на западе &mdash; морем Бохай. Далянь является естественным незамерзающим и глубоководным портом, воротом из других регионов и стран в три северо-восточных провинции Китая и Восточную Внутреннюю Монголию Китая, сухопутным и водным транспортным узлом, одним из важных международных портов. Он известен не только стратегической важностью , но и привлекательными пейзажем.<br />\r\n		&nbsp;&nbsp;&nbsp;&nbsp; Жемчужина северо-восточного Китая, Далянь входит в 500 наиболее благоприятных для проживания городов мира, при этом Даляньская городская агломерация с численностью населения более 6 миллионов человек входит в первую сотню мегаполисов мира.<br />\r\n		&nbsp;&nbsp;&nbsp;&nbsp; Далянь является красивым городом, его городское проетирование уникальной. В этом городе больше 80 плащадей, их темы разнобразные, например известные площадь Чжуншань, площадь Дружбы, площадь Синхай&nbsp;&nbsp; , народная площадь, площадь Олимпиады и др.. На улицах женская конная полиция уже стала красивым и особым пейзажем. Доля площади озеленённых территорий составила выше 40%. Здесь везде &laquo;Зелёный мир&raquo;, демонстрирует лозунг, который выдвинул бывший мэр города Далянь Бо Силай &laquo;Пусть Далянь будет вечно зеленый, красивый, романтический&raquo;. Кроме того, Русские улицы XIX - начала XX вв делают Далянь более красивым.<br />\r\n		&nbsp;&nbsp;&nbsp; Вдоль извилистого морского побережья Даляня расположены красивые достопримечательности, в том числе, известный Государственный туристический курорт &laquo;Цзиньшитань&raquo; , Чёрный риф (образовался карстовый ландшафт&nbsp;&nbsp; 1 миллиард лет назад), Банчуйдао,парк &laquo;Сихай&raquo;, парк &laquo;Лаохутань&raquo;, Парк восточного моря, все они соедиены прибрежнной дорогой, транстпорт очень удобный.<br />\r\n		&nbsp;&nbsp;&nbsp;&nbsp; Здесь Бывшая японско-российская тюрьма, её адрес---район Люйшунь, ул. &laquo;Сянян&raquo;,.номер 139. Она является важным историческим памятником, находящем под Государственной охраной. Кроме того, Военный мемориал и русское кладбище&nbsp;&nbsp; Люйшунь, на горе &laquo;Петушиный гребешок&raquo;(бывшее название Орлиное гнездо), все они рассказывают и напоминают нам, что происходило в последние полвека.<br />\r\n		&nbsp;&nbsp;&nbsp;&nbsp; Далянь известен прекрасными климатическими условиями и богат продуктами, например боярышниками, виноградами, персиками и т.д. Он является &laquo;Родиной яблаков&raquo; Китая. Здесть развитая морская аквакультура, масштабно выращивают больше 50 видов обитателей моря.<br />\r\n		&nbsp;&nbsp;&nbsp; Далянь ещё называется &laquo;Городом футбола&raquo;. Благодяря тренировочной базе любви жителей города к футболу, Далянская футбольная команда завоевала 8 чемпионов по объединённым состязаниям Китае. Кроме того, Далянь, обладающий своим духом спорта, получил звание &laquo;Родина лёгкой атлетики&raquo;, &laquo;Родина плавания&raquo;.<br />\r\n		&nbsp;&nbsp;&nbsp; Благодаря богатым туристическим ресурсам и прекрасным городским средам Далянь является одним из первых городов, которыму было присвоено звание &laquo;Отличный туристический город Китая&raquo; Государственным агенством по туризму и Всемирной организацией по туризму. После получения почетного звания Далянь более &nbsp;известен как в Китае, так за рубежом стал больше превлекать туристов.<br />\r\n		&nbsp;<br />\r\n		<strong><em><span id=\"edu\">Естественно-научное образование</span> </em></strong><br />\r\n		&nbsp;<br />\r\n		&nbsp;&nbsp;&nbsp; В Даляне активно развивается наука, образование и спорт, более 20 ключевых коллеждей и университетов, более 200 различных научно-исследовательских институтов ежегодно выпускают большое число специалистов в области науки и технологий.<br />\r\n		&nbsp;</p>\r\n	<p style=\"text-align: center;\">\r\n		<img alt=\"\" height=\"246\" src=\"/images/editorimages/1285686260949.jpg\" style=\"width: 285px; height: 202px;\" width=\"369\" /><br />\r\n		<br />\r\n		Даляньский музей высшего образования<br />\r\n		<br />\r\n		&nbsp;</p>\r\n	<p style=\"text-align: justify;\">\r\n		<strong><em><span id=\"uni\">Высшие учебные заведения Даляня</span></em></strong><br />\r\n		&nbsp;</p>\r\n	1. &nbsp;Далянский политехнический университет <a href=\"http://www.dlut.edu.cn/en/\">http://www.dlut.edu.cn/en/</a><br />\r\n	2. Далянский морского дела университет <a href=\"http://baike.baidu.com/view/6470.htm\">http://baike.baidu.com/view/6470.htm</a><br />\r\n	3. Северо-восточный финансовый университет <a href=\"http://www.dufe.edu.cn/\">http://www.dufe.edu.cn/#</a><br />\r\n	4. Далянский медицинский университет <a href=\"http://www.dlmedu.edu.cn/english/index.htm\">http://www.dlmedu.edu.cn/english/index.htm</a><br />\r\n	5. Далянский морской университет <a href=\"http://www.dlfu.edu.cn/\">http://www.dlfu.edu.cn/</a><br />\r\n	6. Далянский университет связи и сообщений <a href=\"http://www.djtu.edu.cn/\">http://www.djtu.edu.cn/</a><br />\r\n	7. Ляонинский педагогический университет <a href=\"http://www.lnnu.edu.cn/english/english.htm\">http://www.lnnu.edu.cn/english/english.htm</a><br />\r\n	8. Далянский институт иностранных языков <a href=\"http://edawai.dlufl.edu.cn/\">http://edawai.dlufl.edu.cn/</a><br />\r\n	9. Далянь технологический университет <a href=\"http://en.dlpu.edu.cn/\">http://en.dlpu.edu.cn/</a>\r\n	<p>\r\n		<strong><em><span id=\"school\">Институты Даляня</span></em></strong></p>\r\n	1. Далянский институт национальностей&nbsp; <a href=\"http://www.dlnu.edu.cn/\">http://www.dlnu.edu.cn/</a><br />\r\n	2. Далянский институт информатики и программного обеспечения&nbsp; Neusoft&nbsp; <a href=\"http://www.neusoft.edu.cn/english/index.html\">http://www.neusoft.edu.cn/english/index.html</a><br />\r\n	3. Даляньский институт военно-морской академии Китайской народно- освободительной армии&nbsp;&nbsp; <a href=\"http://baike.baidu.com/view/3980078.htm\">http://baike.baidu.com/view/3980078.htm</a><br />\r\n	4. Ляонинский институт внешней торговли <a href=\"http://www.luibe.edu.cn/\">http://www.luibe.edu.cn/</a><br />\r\n	5. Ляонинская полицейская академия &nbsp;<br />\r\n	6. Далянский профессиональный технический колледж <a href=\"http://www.dlvtc.edu.cn/F01newslistdetailAction.do?classId=1B4E54C6-B1DC-CB40-CF01-F6DD735242C0\">http://www.dlvtc.edu.cn/F01newslistdetailAction.do?classId=1B4E54C6-B1DC-CB40-CF01-F6DD735242C0</a><br />\r\n	7. Далянское профессиональное бизнес училище&nbsp; <a href=\"http://www.dbi.edu.cn/\">http://www.dbi.edu.cn/</a><br />\r\n	8. Далянский&nbsp; колледж программного обеспечения&nbsp; <a href=\"http://www.dlrj.edu.cn/jiaoshoufengcai/home.html\">http://www.dlrj.edu.cn/jiaoshoufengcai/home.html</a><br />\r\n	9. Даляньский институт искусств&nbsp; <a href=\"http://www.neudlac.edu.cn/collage01.html\">http://www.neudlac.edu.cn/collage01.html</a><br />\r\n	10. Далянский&nbsp; колледж&nbsp; внешней торговли <a href=\"http://www.dlflc.com/\">http://www.dlflc.com/</a><br />\r\n	11. Даляньский филиал информационо-технического университета Китайской народно- освободительной армии <a href=\"http://baike.baidu.com/view/10129.html?fromTaglist\">http://baike.baidu.com/view/10129.html?fromTaglist</a><br />\r\n	12. Даляньский филиал университета иностранных языков&nbsp; Китайской народно- освободительной армии <a href=\"http://baike.baidu.com/view/27072.htm\">http://baike.baidu.com/view/27072.htm</a><br />\r\n	13. Даляньская академи&nbsp; изобразительных искусств Лу Синь<br />\r\n	14. Даляньский филиал Ляонинского университета китайской традиционной медицины&nbsp; <a href=\"http://baike.baidu.com/view/35160.htm\">http://baike.baidu.com/view/35160.htm</a><br />\r\n	15. Даляньский филиал Шэньянской консерватории&nbsp; <a href=\"http://www.sycm.com.cn/web_english.asp\">http://www.sycm.com.cn/web_english.asp</a><br />\r\n	16. Даляньская модельная школа искусств&nbsp;<a href=\"http://baike.baidu.com/view/571905.htm\">http://baike.baidu.com/view/571905.htm</a><br />\r\n	17. Далянский институт рабочих и служащих&nbsp;&nbsp; <a href=\"http://baike.baidu.com/view/839148.htm\">http://baike.baidu.com/view/839148.htm</a>\r\n	<p style=\"text-align: justify;\">\r\n		&nbsp;<br />\r\n		<strong><em><span id=\"dialect\">Далянский диалект</span></em></strong><br />\r\n		&nbsp;<br />\r\n		&nbsp;&nbsp;&nbsp; Далянский диалект относится&nbsp; к мандаринскому&nbsp; наречию Jiaoliao. &nbsp;В&nbsp; разговорном языке на северо-востоке Китая не зависимо будь это тон, призношение или лексика, во всем есть своя особенность местного диалекта, а схож с имеющимся в районе залива Бохай напротив полуострова Шандун Яньтайским и другими местными диалектами. &nbsp;Есть, например, в районе города Джуанхэ старейшины в некоторых деревнях, их речь может быть только самими далянцами то и может быть понята.<br />\r\n		&nbsp;<br />\r\n		<strong><em><span id=\"diplomat\">Хронология возникновения дружественных связей с городами - побратимами</span></em></strong><br />\r\n		&nbsp;<br />\r\n		　　1. Китакюсю - Япония 1979.05.01<br />\r\n		　　2. Окленд - Новая Зеландия 1982.03.30<br />\r\n		　　3. Майдзуру - Япония1982.05.08<br />\r\n		　　4. Бремен &ndash; Германия 1985.04.17<br />\r\n		　　5. Leefoer &ndash; Франция &nbsp;1985.11.23<br />\r\n		　　6. Glasgow - &nbsp;Англия &nbsp;1987.11.27<br />\r\n		　　7. Росток - Германия1988.05.16<br />\r\n		　　8. Владивосток - &nbsp;Россия &nbsp;1992.09.10<br />\r\n		　　9. Нуар &ndash; Конго &nbsp;2000.06.09<br />\r\n		　　10. Имари &ndash; Япония 1987.07<br />\r\n		　　11. Инчхон -&nbsp; Южная Корея 1994.04.02<br />\r\n		　　12. Хьюстон &ndash; США &nbsp;1994.06.06<br />\r\n		　　13. Ванкувер - &nbsp;Канада &nbsp;1994.06.17<br />\r\n		　　14. Аделаида &ndash; Австралия 2001<br />\r\n		　　15. Охрид - Македония 2002.04.17<br />\r\n		　　16. Щецин - Польша<br />\r\n		&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 17. Канадзава<br />\r\n		&nbsp;<br />\r\n		<strong><em><span id=\"interest_stuff\">Самое-самое в Даляне и о Даляне</span></em></strong><br />\r\n		&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</p>\r\n	1. Товарищ Jiangzeminназвал Далянь единственной жемчужиной северного&nbsp; Китая<br />\r\n	2. Первый город Китая, вошедший в &ldquo;500 экологических городов мира&rdquo;<br />\r\n	3. Самый красивый романтический город Китая<br />\r\n	4. Входит в первую группу выдающихся туристических городов Китая<br />\r\n	5. Самый прекрасный город - сад Китая<br />\r\n	6. Занимает 1ое место по количеству площадей в Китае<br />\r\n	7. Город с наимньшим количеством китайских велосипедов, мотоциклов и дорожной полиции<br />\r\n	8. Город с крупнейшей городской площадью &nbsp;в Азии - Площадью Синхай<br />\r\n	9. Считается наиболее влиятельным по отношению на Международный фестиваль моды и наиболее популярным в Китае карнавалом<br />\r\n	10. Крупнейший лесной зоопарк Китая расположен здесь - Даляньский Лесной зоопарк<br />\r\n	11. Крупнейший китайский парк морских животных&nbsp; - Далньский морской парк Лаохутан<br />\r\n	12. Алмазы лучшего качества<br />\r\n	13. Самый крупный в Китае Станкостроительный завод и судостроительная верфь<br />\r\n	14. Самый раний Подшипниковый завод&nbsp; Китая&nbsp; - подшипниковый завод Wafangdian<br />\r\n	15. Лучших естестенный морской порт Китая и современный &quot;музей&quot; - Порт-Артур<br />\r\n	16. Самый крупнейший в Азии музей змей<br />\r\n	17. Наиболее хорошо сохранившаяся тюрьма &ndash; галерея&nbsp; - &nbsp;Японо-русская тюрьма Порт-Артура\r\n	<p style=\"text-align: justify;\">\r\n		&nbsp;</p>\r\n</blockquote>\r\n','intro','2010-11-03 09:05:12'),(2,'ИСТОРИЯ ДАЛЯНЯ','<blockquote>\r\n	<div>\r\n		﻿</div>\r\n</blockquote>\r\n<blockquote>\r\n	<p style=\"text-align: center\">\r\n		<strong><strong><em><strong>ИСТОРИЯ </strong><strong>ГОРОДА </strong></em></strong><strong><em>ДАЛЯНЯ</em></strong><br />\r\n		&nbsp;</strong></p>\r\n	<p style=\"text-align: justify\">\r\n		&nbsp;&nbsp;&nbsp;&nbsp; Ещё 6000 лет тому назад наши предки освоили район Далянь. Во власти Династий Чинь и Хань Далянь принадлежал губернии Ляодун. В начале танского времени Далянь принадлежал уезду Цзили Аньдунского края. В ляоское время Далянь принадлежал восточной столице Ляоян. При царстве Вэй и Цзинь Далянь назывался Саньшань. В танское время он был назван Саньшаньпу. В минское и цинское времена город был переименован сначала в Саньшаньхайкоу, а потом&mdash;в Циннивакоу. В 80 годы 19 века на северном берегу залива Даляньвань Жёлтого моря Династия Цин построила причальную эстакаду, форты и казарму подводной мины. Тогда на его месте вырос городок. После того, как городок был захвачен царской Россией, он получил название Циннива. В 1899 году начали называть его городом Далянем. Во время опиумной войны и второй опиумной войны английские захватчики вторгались в этот район. Как главное поле сражения Далянь два раза в новой истории переживал войну: китайско-японскую войну (1894-1895гг.) и русско-японскую войну. После этого он превратился в колонию России и Японии, это продолжалось около половины века, в том числе 40 лет под господством Японии. В августе 1945 года Япония объявила безоговорочную капитуляцию, Далянь был освобождён с победой антифашистской войны, колониалистское господство шло к концу. В сентябре того года был образован подготовительный комитет федерации профессионального союза города. А в октябре были образованы городской комитет КПК и городское правительство. Партийный комитет северо-восточного огруга ЦК КПК назначил товарища Ханьгуана секретарём даляньского комитета. В июле 1946 года горком был реорганизован в комитет округа Люйда. С 1949 по 1956 год социалистическое преобразование в городе в основном закончилось. С 1957 по 1966 год началось всестороннее строительство социализма. С 1966 по 1976 год город испытывал Культурную революцию. С 1976 года вступил в новый период современного строительства социализма. В 1984 году Далянь стал одним из 14 городов Китая, объявленных &quot; свободной экономической зоной&quot; , открытой для иностранных капиталовложений. В 1985 году Далянь обрёл статус города, включённого в государственный план в качестве отдельного хозрасчётного субъекта, получив право управления экономикой на уровне провинции.<br />\r\n		&nbsp;</p>\r\n	<p style=\"text-align: center\">\r\n		&nbsp;<img alt=\"\" height=\"198\" src=\"/images/editorimages/1285687159457.jpg\" width=\"601\" /></p>\r\n	<p style=\"text-align: center\">\r\n		<img alt=\"\" src=\"/images/editorimages/1285687176724.jpg\" style=\"width: 264px; height: 186px\" /><br />\r\n		&nbsp;</p>\r\n	<p style=\"text-align: justify\">\r\n		&nbsp;&nbsp;&nbsp; Городу чуть более 100 лет. По китайским меркам это неприлично мало. До 1898 г. города с таким названием не существовало, был Ляодунский полуостров с военно-морской базой Люй-шунь и мелкие рыбацкие деревушки. В 1896 г. между Императорским Китайским правительством и Русско-Китайским банком был заключен договор о строительстве на китайской территории КВЖД. Южная ветвь КВЖД связывала магистраль с портами Да-лянь-вань и Порт-Артур (Люй-шунь).<br />\r\n		&nbsp;&nbsp;&nbsp; В международных отношениях на Дальнем Востоке в конце 1897-1898 гг. происходили важные события. В ноябре 1897г. Германия оккупировала область на Шанрдунском п-ве и положила таким образом начало новой серии захватов различными державами китайской территории, что получило в исторической литературе название &laquo;Битва за концессии&raquo;. 27 марта 1898 г. Цинское правительство оказалось вынужденным уступить России в аренду южную часть Ляодунского п-ва. Наши предки переименовали Люй-шунь в Порт-Артур и сделали из него отличную базу для Тихоокеанского флота. А восточнее, в бухте с красивым названием &laquo;Виктория&raquo; заложили торговый порт и назвали исконно русским словом &laquo;Дальний&raquo; (по созвучию с названием залива Да-лянь-вань).<br />\r\n		&nbsp;&nbsp;&nbsp; Немалаважную роль в установлении международных торговых связей России с азиатскими странами отводилась г.Дальнему и новому порту. Условиями конвенции о постройке порта оговаривалось, что, кроме одной из бухт, предназначенной для нужд российского и китайского флота, все остальное пространство нового порта должно считаться свободным, открытым для иностранной торговли.<br />\r\n		&nbsp;&nbsp;&nbsp; Разработкой плана нового города занимался известный архитектор К.Г.Сколимовский. Он ориентировался на французский тип городов с двумя центральными площадями и радиально расходящимися от них улицами.<br />\r\n		&nbsp;&nbsp; Российские инженеры распланировали город для дальнейшей застройки, начали углублять гавань, строить причалы, волноломы и верьфи. В 1893г. соединили оба порта железной дорогой с Харбином.<br />\r\n		&nbsp;&nbsp;&nbsp; Вся территория города была разделена на три самостоятельные части: Европейский, Китайский город и Административный городок, однако они были связаны друг с другом и представляли единую градостроительную струтуру. Когда Административный городок был практически завершен, а застройка остальных частей города только развертывалась широким фронтом, началась Русско-Японская война, в которой Россия, как известно потерпела поражение.<br />\r\n		&nbsp;&nbsp; Согласно Портсмутскому договору 1905г, город Дальний вместе с Южной веткой КВЖД отошел Японии. С этого времени его стали называть на японский манер - &laquo;Дайрен&raquo;.&nbsp; Начался новый этап в развитии города, спланированного и начатого русскими инженерами.<br />\r\n		&nbsp;&nbsp;&nbsp; К чести японских инженеров, они практически сохранили разработанный Сколимовским генплан, а застройку продолжили по этому же проекту. За 40 лет японского владычества Дальний стал главным портом в Манчжурии. Здесь они сосредоточили практически все свои военные и административные учреждения. Дальний выглядел на редкость красивым и многонациональным.<br />\r\n		&nbsp;&nbsp;&nbsp; В 20-40-е годы в Дальнем выросли огромные кварталы, застроенные шикарными особняками японской буржуазии, офицерства и чиновничества. Многие крупные общественные и Административные здания, построенные японцами, сохранились в Даляне до настоящего времени. В одном из них, например, размещается городская мерия.<br />\r\n		&nbsp;&nbsp;&nbsp; Название &laquo;Дайрен&raquo; перестало быть актуальным 23августа1945г. В этот день в Дальнем и Порт-Артуре высадились советские десантники, и японские гарнизоны сдались. Дальний вновь перешел под юрисдикцию России (теперь уже СССР), а в начале 1950-х годов советское правительство передало КВЖД и в том числе город Дальний Китайской Народной Республике.<br />\r\n		&nbsp;&nbsp;&nbsp; Присутствие СССР закончилось в 1954г., но наименования улиц и памятники русской архитектуры до сих пор напоминают о присутствии Сов.Союза в прошлом. В 1950г. после образования нового Китая, Далянь был переименован в город Люйда. В1981г. город снова был переименован в город Далянь.<br />\r\n		&nbsp;&nbsp; До 1955г. россиян здесь было много. Самую большую площадь Даляня назвали именем Сталина и воздвигли на ней высокий памятник воинам-освободителям. Некоторое время назад памятник перевезли в Порт-Артур, а площадь переименовали в Народную.<br />\r\n		&nbsp;</p>\r\n	<p style=\"text-align: center\">\r\n		<strong><em><strong><em>История Порт-Артура (Люйшуня)&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </em></strong></em></strong><br />\r\n		&nbsp;</p>\r\n	<p style=\"text-align: center\">\r\n		&nbsp;<img alt=\"\" height=\"225\" src=\"/images/editorimages/1285687435600.jpg\" style=\"width: 573px; height: 171px\" width=\"1219\" /></p>\r\n	<p style=\"text-align: justify\">\r\n		<br />\r\n		&nbsp;&nbsp;&nbsp;&nbsp; История Даляня неразрывно связана с соседним городом Люйшунем (Порт-Артур), расположенным в 50 км к юго-западу от него. С 1880-ого года&nbsp; этот город стал развиваться, как крепость для защиты морских границ Пекина и&nbsp; как главная военно-морская база северо-флотских группировок Китая.<br />\r\n		&nbsp;&nbsp;&nbsp;&nbsp; Город Люйшунь (в переводе с китайского - &laquo;удобный для поездок&raquo;) расположен в южной части Ляодунского п-ва в провинции Ляонин и занимает территорию общей площадью 506 кв.км., линия морского побережья составляет 174 км. Население города 210тыс.чел., в том числе 80 тыс.чел. проживает в городе, остальные заняты в сельском хозяйстве. С 1981г. административно подчинен г. Даляню, является его районом.<br />\r\n		&nbsp;&nbsp;&nbsp;&nbsp; Происхождение названия города Порт-Артур таково: во время второй опиумной войны Англии и Франции с Китаем (1856-1860гг.) небольшая китайская крепость Люйшунь была захвачена англичанами. Они и переименовали крепость в честь принца Артура Коннаутского.<br />\r\n		&nbsp;&nbsp;&nbsp;&nbsp; Первое поселение на этом месте возникло во времена династии Восточная Цзинь. Первый император этой династии приказал строить Великую стену. Затем он постепенно перерос в рыбацкий посёлок, назывался Пастью льва. Первое упомянание о городе под названием Люйшунь, датировано в первый период правления династии Мин 1368г-1644г. В то время император Мина отправил двух генералов с морской армией на отбитие этой земли, они приехали из провинции Шандонь сюда благополучно через закрытое море Бохэ, и переименовали это место в Люйшунь.<br />\r\n		&nbsp;&nbsp;&nbsp;&nbsp; Люйшунь находится в западной оконечности Ляодунского полуострова, в гавани с узким входом, со всех сторон окруженной сопками, будто специально созданная для укрытия военных судов от противника. Поэтому он называется горлом Пекина. В 1880г последняя династия Чинь приказала строить форты и доки, и построили одну из пяти крупнейших военных портов в мире.<br />\r\n		&nbsp;&nbsp;&nbsp;&nbsp; Чтобы занять это важное военное место, в 1894г между Японией и Китаем произошла большая война, японские захватчики наступили в город и истребили китайцев за трое суток, в то время в Люйшуне жило всего 20 тыс человек, после истребления только остались 36 человек. Правительство Чинь вынуждено было подписать Симоносетский договор, по которому японцы взяли Люйшунь в аренду. Такое поведение не понравилось Германии, России и Франции. В конце-концов японцы вернули Китаю Люйшунь. А Россия, продолжая развивать своё присутствие на Дальнем востоке, предприняла ряд шагов по аренде Ляодунского полуострова. Договоренность была достигнута в 1898г. С этого времени Порт-артур начал усиленно развиваться в качестве основной базы российского флота на тихом океане. Излишне говорить, что такое положение не понравилось Японии. Они тоже хотели занять Порт-Арту, к тому же они считали свои&nbsp; вооружённые силы сильнее. Так и началась русско-японская война в феврале 1904г.<br />\r\n		&nbsp;&nbsp;&nbsp;&nbsp; Чтобы защитить порт от обхода, россиские войска построили полукруглую линию оборонны в 25 км. на всех сопках, которая включает много фортов, батарей, траншей, окопов, &nbsp;заграждение с током, подкопы, минное поле.<br />\r\n		&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Во время войны, чтобы захватить господство на море и забить фарватер, японцы устроили три заградительные линии за портом. В первую линию отправили отряд смертников на старых пароходах с большими камнями наступать на порт, потом разбомбили эти пароходы в фарватере, японцы всего подорвали 17 пароходов, которые играли роль загрждения. Вторую линию около порта в море закладывали мины, которые являлись угрозой россискому войску, командующий Мокаровфлота тихого океана наскочил на мину и погиб в 2 милях&nbsp; от берега. Третья линия - около круглого острова в близком море засели в засаду, чтобы переградить россиские корабли. 10 августа 1904 г россиский флот пробился из окружения к Владивостоку, но подвергся нападением японских кораблей, поэтому эвакуировались в порт. Самые ожесточённые бои были в Северной крепости на восточной горе гребня, где место гибели Контроченка, на Форте сторожевой вышки, на Крепости на горе двух драконов, на высоте 203 и т.д.&nbsp;<br />\r\n		&nbsp;&nbsp;&nbsp; В войне на стороне России погибли около 20 тыс. солдат, а настороне Японии --- около 40 тыс. После войны военные главы двух сторон встретились на переговорах, генерал Японии надменно принял лошадь в качестве трофеи от командующего Стшэра и сфотографировался. Помимо людских и материальных потерь в ходе боевых действий, России пришлось принять условия Портсмутского договора, по которому&nbsp; японии отошли Ляодунский полуостров, южная Манчжурская ж/д, плюс пол-Сахалина впридачу. Таким образом, японская армия заняла Порт-Артур до 1945-ого года.<br />\r\n		&nbsp;&nbsp;&nbsp;&nbsp; В ночь 9-ого августа 1945 г заблаговременно сосредоточенные на Дальнем Востоке в Забайкалье силы начали боевые действия против Японии. Японцы сопротивлялись умело и ожесточённо. Однако после того, как Красная армия взяла Хайларский укрепрайон и преодолела казавшийся неприступным Большой Хинган, их мораль покатилась вниз. Высадку 23-ьего августа русских бойцов на парашютах и посадочным способом (с гидросамолётов) в Порт-Артуре и Дальнем японцы восприняли как логичное следствие прокатившегося с Севера сквозь укреленные позиции наступления, и отдали порты без боя.<br />\r\n		&nbsp;&nbsp;&nbsp;&nbsp; В истории России Порт-Артур известен как - российская военно-морская база, место героического сражения в период русско-японской войны. С 1945г. по апрель 1955г. в Порт-Артуре располагалась база ВМФ СССР. В 1945г. СССР заключил с гоминдановским правительством договор, по которому военно-морская база Порт-Артура сдавалась в аренду на 30лет. Но через несколько лет Чан Кайши сбежал на Тайвань, а товарищи из руководства КПСС, чтобы не обижать товарищей из КПК, в 1955г. вывели из Порт-Артура все войска.<br />\r\n		&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 1июня 1996 года было принято решение о частичной открытости города, прежде всего для китайских граждан. Люйшунь сегодня является одной из крупнейших военно-морских баз КНР.<br />\r\n		&nbsp;</p>\r\n	<p style=\"text-align: center\">\r\n		<strong><em>Исторический очерк о городе Далянь</em></strong><br />\r\n		&nbsp;</p>\r\n	<p style=\"text-align: justify\">\r\n		500 миллионов лет назад: район города Далянь был мелководным морем.<br />\r\n		200 тысяч лет назад: постепенно появилась суша.<br />\r\n		6000 лет назад: предки -- Сушэньцыобразовали самую раннюю цивилизацию.<br />\r\n		3000 лет назад: Далянь вошёл в эпоху бронзового века.<br />\r\n		2300 лет назад: Ханьцы перешли в район Даляня.<br />\r\n		В 640 году н. э.: В начале династии Тан Далянь назывался &quot;Саньшаньпу&quot;, в середине династии был переименован в &quot;Циннипу&quot;.<br />\r\n		В 17 - 19 веках: После войны между Китаем и Японией, Япония заняла Ляодунский полуостров.<br />\r\n		В 1895 году: Правительство Цина выкупило Ляодунский полуостров за большое количество серебра.<br />\r\n		В 1898 году: Россия арендовала на 30 лет Залив &quot;Даляньвань&quot; и Порт-Артур.<br />\r\n		В 1903 году: Был построен город в европейском стиле в центре с Музыкальной площадью (Площадь Николая).<br />\r\n		В 1904 году: Началась война между Россией и Японией.<br />\r\n		В 1905 году: Россия отказалась от Ляодунского полуострова в пользу Японии.<br />\r\n		В 1945 году: Япония капитулировала, советские десантники высадились в Даляне и Порт-Артуре, создалось городское народное правительство.<br />\r\n		В 1950 году: Далянь, соединённый с Порт-Артуром, был переименован в город Люйда.<br />\r\n		В 1955 году: Советские войска были выведены из Даляня.<br />\r\n		В 1981 году: Город Люйда был переименован в город Далянь.<br />\r\n		В 1984 году: Далянь включён в число первых открытых приморских городов.<br />\r\n		В 1985 году: Государство дало разрешение Даляню стать независимым городом.<br />\r\n		В 1994 году: Далянь стал вторым по важности городом провинции.<br />\r\n		В 1999 году: Городу Далянь исполнилось 100 лет.<br />\r\n		<br />\r\n		&nbsp;</p>\r\n</blockquote>\r\n','history','2010-10-04 16:14:57'),(3,'保险','<table align=\"center\" border=\"1\" cellpadding=\"1\" cellspacing=\"1\" style=\"width: 400px;\">\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				&nbsp;</